    public static List<char> list = PuzzleInput.ToCharArray().ToList();

    
    public static void SendFloor(List<char> input)
    {
        int floor = 0;
        int position = 0;
        bool found = false;

        foreach (var commando in input)
        {
            position++;
            
            if (floor < 0 && found == false)
            {
                Console.WriteLine("Position of first basement character: "+position);
                found = true;
            }
            
            if (commando == '(')
            {
                floor++;
            }else if (commando == ')')
            {
                floor--;
            }
        }
        Console.WriteLine("Santa's Position: "+floor);
    }
